import React, { Component, useState  } from 'react';
import AddIcon from '@material-ui/icons/Add';
import Fab from '@material-ui/core/Fab';
import Zoom from '@material-ui/core/Zoom';
import axios from "axios";
import DeleteIcon from '@material-ui/icons/Delete';
import Header from "./Header";
import Footer from "./Footer";


function CreateArea(props) {
  const [note, setNote] = useState({
    title: "",
    content: ""
  });

  const [expand, setExpand] = useState(false);

  function handleChange(event) {
    const { name, value } = event.target;

    setNote(prevNote => {
      return {
        ...prevNote,
        [name]: value
      };
    });
  }

  function submitNote(event) {
    props.onAddNote(note);
    setNote({
      title: "",
      content: ""
    });

    axios.post("http://localhost:3001/add", note)
            .then(res => console.log(res.data));

    event.preventDefault();
  }

  function onExpand(){
    setExpand(true);
  }

  return (
    <div>
      <form className="create-note" >
        <input
          name="title"
          onChange={handleChange}
          value={note.title}
          placeholder="Title"
          style={{display: !expand ? "none":null}}
        />
        <textarea
          name="content"
          onChange={handleChange}
          value={note.content}
          placeholder="Take a note..."
          rows={!expand ? "1":"3"}
          onClick={onExpand}
        />
        <Zoom in={!expand ? false:true}><Fab onClick={() => {submitNote}}><AddIcon/></Fab></Zoom>
      </form>
    </div>
  );
}

const Note = props => (
  <div className="note">
      <h1>{props.title}</h1>
      <p>{props.content}</p>
      <button onClick={()=>{ props.deleteNote(props.note._id) }}><DeleteIcon/></button>
    </div>
)

export default class Home extends Component {
  constructor(props) {
    super(props);

    this.deleteNote= this.deleteNote.bind(this)

    this.state = {notes: []};
  }



  componentDidMount() {
    axios.get('http://localhost:3001/')
      .then(response => {
        this.setState({ notes: response.data })
      })
      .catch((error) => {
        console.log(error);
      })
  }


  addNote(note){
    axios.get('http://localhost:3001/')
      .then(response => { console.log(response.data)});
      
    console.log( this.state.notes);
    let joined = this.state.notes.concat(note);

    this.setState({ notes: joined })
  }

  deleteNote(id) {
    axios.delete('http://localhost:3001/'+id)
      .then(response => { console.log(response.data)});

    this.setState({
      notes: this.state.notes.filter(el => el._id !== id)
    })
  }

  noteList() {
    return this.state.notes.map(currentnote => {
      return <Note
        note={currentnote}
        key={currentnote._id}
        id={currentnote._id}
        title={currentnote.title}
        content={currentnote.content}
        deleteNote={this.deleteNote}
      />      
    })
  }

  render() {
    return (
      <div>
        <Header />
        <CreateArea onAddNote={this.addNote} />
        { this.noteList() }
        <Footer />
      </div>
    )
  }
}
